<?php	// -- общий класс-предок для хелперов		class app_helpner {				protected $config = array();				public function __construct($config_arr = null) {			$this->config($config_arr);		}				// -- геттер, он же сеттер для массива конфига		public function config($label, $value = null) {			if($value === null && !empty($label) && is_string($label)) {				// -- рабатает как геттер				return isset($this->config[$label]) ? $this->config[$label] : null;			} elseif(!empty($label) && is_array($label)) {				// -- массовый сеттер				foreach($label as $sub_label => $item) {					if(!empty($sub_label)) {						$this->config[$sub_label] = $value;					}				}			} elseif(!empty($label) && $value !== null && is_string($label)) {				// -- одиночный сеттер				$this->config[$label] = $value;			}		}			}?>